-- Migração 009: Atualizar sponsorship_counterparts com explicações completas do PDF UTMB 2025
-- Remove os registros antigos e reinsere com textos completos baseados no PDF

-- Deletar todos os sponsorship_counterparts existentes
DELETE FROM sponsorship_counterparts;

-- Inserir contra-partidas completas para Title (R$ 1.5 MM)
DO $$
DECLARE
  title_tier_id UUID;
BEGIN
  SELECT id INTO title_tier_id FROM sponsorship_tiers WHERE name = 'Title' AND value_brl = 1500000.00 LIMIT 1;
  
END $$;

